	/**
	 * This function is called periodically during operator control
	 */
	public void teleopPeriodic() {
		double speed;
		
		Scheduler.getInstance().run();
		loopCounter += 1;
		speed=Math.max(DrivelineSubsystem.getLeftEncoder().getRate(),
						DrivelineSubsystem.getRightEncoder().getRate());
		nttable.getEntry("Robot-speed:").setNumber(speed);

		controlBoxSubsystem.readBits(); // read the switches on the external control box
		controlBoxSubsystem.printBits();

		boolean shift = false;
		nttable.getEntry("Robot-shift_control:").setNumber(shift_control);
		switch(shift_control)
		{
			case 0: // Auto
				if (isHighGear() )
				{
					shift = speed>shift_down_speed;
				} else {
					shift = speed>shift_up_speed;
				}
				break;
			case 1: // low
				shift = false; 
				break;
			case 2: // high
				shift = true;
				break;
		}
		nttable.getEntry("Robot-shift:").setBoolean(shift);
		setGear(shift);
	}

