    public void setGear(boolean gearValue) {
		RobotMap.shifter.set(gearValue ? Value.kForward : Value.kReverse);
		isInHighGear = gearValue;
		nttable.getEntry("isInHighGear:").setBoolean(isInHighGear);
    }

